class Image:
    def __init__(self, file="",data=""):
        if file != "":
            self.file = tkinter.PhotoImage(file=file)
        elif data != "":
            self.file = tkinter.PhotoImage(data=data)
    def _gettkIG(self):
        return {"file":self.file}

def RGB(red,green,blue):
    return "#%02x%02x%02x" % (red, green, blue)

