from __future__ import print_function, division


def addition(num1, num2):
    """Perform addition between two numbers.
    Args:
        num1 (int): integer number
        num2 (int): integer number
    Returns:
        addition_result: integer number.
    """
    addition_result = num1 + num2
    return addition_result


def subtraction(num1, num2):
    """Perform subtraction between two numbers.
    Args:
        num1 (int): integer number
        num2 (int): integer number
    Returns:
        subtraction_result: integer number.
    """
    subtraction_result = num1 - num2
    return subtraction_result


def multiplication(num1, num2):
    """Perform multiplication between two numbers.
    Args:
        num1 (int): integer number
        num2 (int): integer number
    Returns:
        multiplication_result: integer number.
    """
    multiplication_result = num1 * num2
    return multiplication_result


def division(num1, num2):
    """Perform division between two numbers.
    Args:
        num1 (int): integer number
        num2 (int): integer number
    Returns:
        division_result: integer number.
    """
    division_result = num1 / num2
    return division_result


def modulo(num1, num2):
    """Perform modulo between two numbers.
    Args:
        num1 (int): integer number
        num2 (int): integer number
    Returns:
        modulo_result: integer number.
    """
    modulo_result = num1 % num2
    return modulo_result
